var tap = require('../tap')
var ok = require.resolve('../tap/test/test/ok.js')

for (var i = 1; i <= 10; i++) (function (i) {
  tap.test('test #' + i, function (t) {
    var ret
    for (var j = 0; j < i; j++) {
      ret = t.spawn(process.execPath, [ok], 'sub #' + j)
    }
    return ret
  })
})(i)
